package com.wuji1626.themis.variable;

public class PrimitiveTypeByte {
    
    	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte bt;
		
		bt = 65;
		System.out.println("数值 = " + bt);
		
		bt = 'A';
		System.out.println("字母A的ASCII十进制码 = " + bt);
		
		bt = '\0';
		System.out.println("特殊字符的空字符码 = " + bt);
				
	}
    
}
