package com.wuji1626.themis.variable;

public class PrimitiveTypeShort {
    
    	public static void main(String[] args) {
		// TODO Auto-generated method stub
		short st;
		
		st = 65;
		System.out.println("一般数值 = " + st);
		
		st = 'A';
		System.out.println("字母A的ASCII十进制码 = " + st);
		
		st = '中';
		System.out.println("汉字字符十进制码 = " + st);
		
		st = '\0';
		System.out.println("特殊字符的空字符码 = " + st);
				
	}
    
}
